package main

import (
	"strings"
	"testing"
)

func TestAnalyzeDiff(t *testing.T) {
	tests := []struct {
		name     string
		diff     string
		expected []FileGroup
	}{
		{
			name: "single file change",
			diff: `diff --git a/main.go b/main.go
index 1234567..abcdefg 100644
--- a/main.go
+++ b/main.go
@@ -1,5 +1,6 @@
 package main

 import "fmt"
+import "os"

 func main() {`,
			expected: []FileGroup{
				{
					Type:        "feat",
					Description: "Add import dependency",
					Files:       []string{"main.go"},
				},
			},
		},
		{
			name: "multiple related files",
			diff: `diff --git a/user.go b/user.go
index 1234567..abcdefg 100644
--- a/user.go
+++ b/user.go
@@ -1,3 +1,8 @@
 package main

-// User struct
+type User struct {
+    ID   int
+    Name string
+}
+
+// User struct with fields
diff --git a/user_test.go b/user_test.go
index 1234567..abcdefg 100644
--- a/user_test.go
+++ b/user_test.go
@@ -0,0 +1,10 @@
+package main
+
+import "testing"
+
+func TestUser(t *testing.T) {
+    user := User{ID: 1, Name: "test"}
+    if user.ID != 1 {
+        t.Error("Expected ID to be 1")
+    }
+}`,
			expected: []FileGroup{
				{
					Type:        "feat",
					Description: "Add User struct and tests",
					Files:       []string{"user.go", "user_test.go"},
				},
			},
		},
		{
			name: "unrelated changes",
			diff: `diff --git a/auth.go b/auth.go
index 1234567..abcdefg 100644
--- a/auth.go
+++ b/auth.go
@@ -1,3 +1,5 @@
 package main

+// Auth module
+
 func authenticate() {}
diff --git a/database.go b/database.go
index 1234567..abcdefg 100644
--- a/database.go
+++ b/database.go
@@ -1,3 +1,5 @@
 package main

+// Database module  
+
 func connect() {}`,
			expected: []FileGroup{
				{
					Type:        "docs",
					Description: "Add module documentation",
					Files:       []string{"auth.go", "database.go"},
				},
			},
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			result := AnalyzeDiff(tt.diff)
			if len(result) != len(tt.expected) {
				t.Errorf("Expected %d groups, got %d", len(tt.expected), len(result))
				return
			}

			for i, group := range result {
				if group.Type != tt.expected[i].Type {
					t.Errorf("Expected type %s, got %s", tt.expected[i].Type, group.Type)
				}
				if !strings.Contains(group.Description, tt.expected[i].Description) {
					t.Errorf("Expected description to contain %s, got %s", tt.expected[i].Description, group.Description)
				}
				if len(group.Files) != len(tt.expected[i].Files) {
					t.Errorf("Expected %d files, got %d", len(tt.expected[i].Files), len(group.Files))
				}
			}
		})
	}
}

func TestGetGitDiff(t *testing.T) {
	tests := []struct {
		name        string
		expectError bool
	}{
		{
			name:        "get git diff",
			expectError: false,
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			diff, err := GetGitDiff()
			if tt.expectError && err == nil {
				t.Error("Expected error but got none")
			}
			if !tt.expectError && err != nil {
				t.Errorf("Unexpected error: %v", err)
			}
			if !tt.expectError && diff == "" {
				// This is acceptable for clean repositories
			}
		})
	}
}

func TestGroupFilesByContext(t *testing.T) {
	tests := []struct {
		name     string
		files    []ChangedFile
		expected []FileGroup
	}{
		{
			name: "related test and implementation files",
			files: []ChangedFile{
				{Path: "user.go", ChangeType: "modified"},
				{Path: "user_test.go", ChangeType: "added"},
			},
			expected: []FileGroup{
				{
					Type:        "feat",
					Description: "User functionality",
					Files:       []string{"user.go", "user_test.go"},
				},
			},
		},
		{
			name: "documentation files",
			files: []ChangedFile{
				{Path: "README.md", ChangeType: "modified"},
				{Path: "docs/api.md", ChangeType: "added"},
			},
			expected: []FileGroup{
				{
					Type:        "docs",
					Description: "Documentation updates",
					Files:       []string{"README.md", "docs/api.md"},
				},
			},
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			result := GroupFilesByContext(tt.files)
			if len(result) != len(tt.expected) {
				t.Errorf("Expected %d groups, got %d", len(tt.expected), len(result))
			}
		})
	}
}
